import React, { useEffect, useState } from 'react'
import SchoolCard from './SchoolCard';

const Body = () => {
  const [filteredData,setfilteredData] = useState([]);
  useEffect(()=>{
    fetchData();
  },[])
  const fetchData = async()=>{
    const data = await fetch("http://localhost:1337/api/schools"); // starpi local  
    const json = await data.json();
    console.log("data >>>>>>>>>",json.data);
    setfilteredData(json.data);
  }
  return (
    <div className='mt-[72px] mx-[160px] '>
      <div className='p-4 m-4 w-10/12 flex flex-wrap justify-center  m-auto border-red'>
      <button className='m-4 p-4 hover:bg-[#3481E8] items-center justify-center bg-[#D0D0D0] rounded-full text-black'>
          All
        </button>
      {filteredData.map((e) => (

        <button className='m-1 p-4 flex hover:bg-[#3481E8] items-center text-[18px] justify-center bg-[#D0D0D0] rounded-full text-black'>
          {e.attributes.Tags === "plantation"?"Plantation Day":e.attributes.Tags === "Alumni" ? "Alumni association":e.attributes.Tags === "annual"?"Annual day":e.attributes.Tags === "NCC"?"NCC(National cadet corps)":e.attributes.Tags === "labs"?"Science labs":e.attributes.Tags.includes("cleanliness")?"Cleanliness drive":""}
        </button>
          ))}

      </div>
      <div className='p-4 m-4 flex flex-wrap gap-4 justify-center w-10/12 m-auto'>
        {filteredData.map((e) => (
            <SchoolCard resData={e} />
          ))}
      </div>

      <button className='w-[154px] h-[60px] ml-[680px] mt-[100px] border border-solid font-poppins border-[#F54E39] rounded-lg text-[#F54E39]'>
            View More
      </button>
    </div>
  )
}

export default Body
