import React, { useEffect, useState } from 'react'
import SchoolCard from './SchoolCard';

const Body = () => {
  const [filteredData,setfilteredData] = useState([]);
  useEffect(()=>{
    fetchData();
  },[])
  const fetchData = async()=>{
    const data = await fetch("http://localhost:1337/api/schools"); // starpi local  
    const json = await data.json();
    console.log("data >>>>>>>>>",json.data);
    setfilteredData(json.data);
  }
  return (
    <div className='mt-[72px] mx-[160px] border border-black'>
      <div className='w-10/12 flex justify-center border border-red'>
      <button className='m-4 p-4 items-center justify-center bg-[#D0D0D0] rounded-full text-black'>
          All
        </button>
      {filteredData.map((e) => (

        <button className='m-1 p-3 flex items-center text-[18px] justify-center bg-[#D0D0D0] rounded-full text-black'>
          {e.attributes.Tags === "plantation"?"Plantation Day":e.attributes.Tags === "Alumni" ? "Alumni association":e.attributes.Tags === "annual"?"Annual day":e.attributes.Tags === "NCC"?"NCC(National cadet corps)":e.attributes.Tags === "labs"?"Science labs":e.attributes.Tags.includes("cleanliness")?"Cleanliness drive":""}
        </button>
          ))}

      </div>
      <div className='flex flex-wrap justify-center w-6/12 m-auto bg-gray-300'>
        {filteredData.map((e) => (
            <SchoolCard resData={e} />
          ))}
      </div>
    </div>
  )
}

export default Body
